INSERT INTO `post_info` VALUES ('1020', '2', '1', '全国商务英语翻译（ETTBL）考试喜报', null, '\n  \n  在2011年11月的全国商务英语翻译（ETTBL）考试中，外语学院考点在各级笔译、口译考试中，取得了可喜的成绩，整体通过率高达90.2%，其中初级笔译和高级笔译通过率为100%，在全国名列前茅。特此向这些同学表示热烈的祝贺！ \n  外语学院考点是全国商务英语翻译（ETTBL）考试中心在黑龙江省的唯一考点。 \n  2012年上半年考试正在火热报名中，欢迎广大学子积极报考！校内考生近期报考享有优惠！ \n  报名时间：3月12日至4月20日 \n  报名地点：外语学院外环办（北区主楼：0517办公室） \n  报名电话：0451-84865049、15104548691 联系人：刘老师、李老师 \n  外语学院 \n  2012年3月 \n     \n \n', '0', '0', '0', '2012-03-19 00:00:00', null, '0', null, '0', null);
