INSERT INTO `post_info` VALUES ('1052', '2', '1', '外语学院成为全国商务英语翻译证书（ETTBL）培训考试点', null, '\n  \n  全国商务英语翻译考试（ETTBL）是国内唯一一家系统的商务英语翻译考试，侧重认证商务翻译人才；考试突出商务英语翻译专业性和实用性，强调翻译技能，重点培养学生的市场适应和竞争能力；证书网上注册，全国范围内有效，所有通过认证者纳入中国商务人才网，助推就业，是商务英语职业岗位的权威凭证，极具竞争力。 \n  2011年7月26日，全国商务英语翻译考试培训中心授权外语学院考点设立考点。该考点是唯一一家面向黑龙江省广大考生的国内考点，依托于哈尔滨商业大学良好的商务经贸环境，拥有一流的语音听力设备，优秀的师资队伍和丰富的商务英语翻译考试和培训资源。 \n  全国商务英语翻译考试（ETTBL）成功进入黑土地，必将给黑龙江省广大学子提供一条检验和认证商务职业英语实践水平的通道，促进自身国际商务贸易沟通能力的提高，更好地服务于黑土地的经济发展和人才培养。 \n  2011年下半年考试将于2011年11月19日和20日进行。报名工作从现在开始。 \n  报名地点：哈尔滨商业大学外语学院外环办（北区主楼：0517办公室） \n  报名电话：0451-84865049 \n  报名联系人：刘宓 \n \n', '0', '0', '0', '2011-09-21 00:00:00', null, '0', null, '0', null);
