INSERT INTO `post_info` VALUES ('123', '2', '1', '哈尔滨商业大学实验综合楼弱电设备采购招标公告', null, '\n  \n  招标编号：HTC-182306 \n  1. 招标条件 \n  哈尔滨商业大学实验综合楼弱电设备采购已经相关部门批准建设，招标人为哈尔滨商业大学。建设资金来自中西部高校基础能力建设工程专项资金、地方配套资金和建设单位自筹。项目已具备招标条件，黑龙江省招标公司受招标人的委托，现对该项目进行公开招标。 \n  2. 项目概况与招标范围 \n  2.1项目名称：哈尔滨商业大学实验综合楼弱电设备采购 \n  2.2交货地点：哈尔滨商业大学江北校区B区； \n  2.3采购内容：POE交换机20台；接入交换机22台；光交换机1台；光模块44只；配线架53个；光纤收发器4对等。具体参数见招标文件。 \n  2.4 交货期：合同签定后10个日历日。 \n  3、投标人资格要求： \n  3.1投标人必须提供营业执照副本； \n  3.2投标人须提供生产厂商二年硬件维保服务和原厂安装服务。 \n  3.3本次招标不接受联合体投标。  \n  3.4 一个制造商对同一品牌同一型号的设备，仅能委托一个代理商参加投标。 \n  4、投标登记及购买招标文件 \n  4.1凡有意参加投标者，请于2018年8月31日至2018年9月6日(法定公休日、法定节假日除外)，每日上午9:00时至11:00时，下午13:30时至16:30时(北京时间，下同)，持授权委托书及上述相关资格证明材料加盖公章复印件到黑龙江省招标公司（哈尔滨市南岗区汉水路180号）306室获取招标文件，逾期不予受理。 \n  4.2招标文件售价300元，售后不退。 \n  5.投标文件的递交 \n  5.1投标文件递交的截止时间(投标截止时间)为2018年9月20日9时00分，地点为黑龙江省招标公司（哈尔滨市南岗区汉水路180号）二楼会议室。 \n  5.2逾期送达的、未送达指定地点的或者不按照招标文件要求密封的投标文件，招标人予以拒收。 \n  6、发布公告的媒介本公告在中国招标投标公共服务平台上发布。 \n  7、联系方式 \n  招标人：哈尔滨商业大学 \n  联系人：刘欢 \n  联系电话：0451-84892112 \n  招标代理机构：黑龙江省招标公司 \n  地址：哈尔滨市开发区汉水路180号 邮编：150090 \n  联系人：申明慧 周岩青 电话：0451-82364710/15  \n  传真：0451-82364719 电子邮箱hljzb2@126.com  \n  开户银行：招商银行股份有限公司哈尔滨开发区支行 账 号：451902390110302 \n  开户名称：黑龙江省招标公司 \n   \n \n', '0', '0', '0', '2018-08-31 00:00:00', null, '0', null, '0', null);
