INSERT INTO `post_info` VALUES ('2054', '2', '1', '关于2018年度省级优秀团员、优秀团干部、五四红旗团委（团支部）拟推荐情况的公示', null, '<div id=\"vsb_content\">\n <div class=\"v_news_content\"> \n  <p><span style=\"font-family: 宋体; font-size: 16px;\"><span style=\"font-family: 宋体;\">各学院团委：</span></span></p> \n  <p style=\"line-height: 37px; text-indent: 32px;\"><span style=\"color: rgb(51, 51, 51); font-family: tahoma; font-size: 16px;\"><span style=\"font-family: 宋体;\">按照团省委</span></span><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 16px;\"><span style=\"font-family: 宋体;\">《</span></span><span style=\"color: rgb(51, 51, 51); font-family: tahoma; font-size: 16px;\"><span style=\"font-family: 宋体;\">关于申报</span></span><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 16px;\">2018</span><span style=\"color: rgb(51, 51, 51); font-family: tahoma; font-size: 16px;\"><span style=\"font-family: 宋体;\">年度</span></span><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 16px;\">“</span><span style=\"color: rgb(51, 51, 51); font-family: tahoma; font-size: 16px;\"><span style=\"font-family: 宋体;\">全省优秀共青团员</span></span><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 16px;\">”“</span><span style=\"color: rgb(51, 51, 51); font-family: tahoma; font-size: 16px;\"><span style=\"font-family: 宋体;\">全省优秀共青团干部</span></span><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 16px;\">”“</span><span style=\"color: rgb(51, 51, 51); font-family: tahoma; font-size: 16px;\"><span style=\"font-family: 宋体;\">全省五四红旗团委（团支部）</span></span><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 16px;\">”</span><span style=\"color: rgb(51, 51, 51); font-family: tahoma; font-size: 16px;\"><span style=\"font-family: 宋体;\">的通知</span></span><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 16px;\"><span style=\"font-family: 宋体;\">》的工作部署，经校团委召集各学院团委联合推荐，结合</span></span><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 16p