INSERT INTO `post_info` VALUES ('24', '2', '1', '哈尔滨商业大学校门诊部及卫生所医保HIS系统及设备采购安装项目竞争性谈判公告', null, '\n  \n  中资国际招标有限责任公司受哈尔滨商业大学的委托，对哈尔滨商业大学校门诊部及卫生所医保HIS系统及设备采购安装项目进行国内竞争性谈判，现邀请国内合格供应商前来参加。 \n  一、项目名称：哈尔滨商业大学校门诊部及卫生所医保HIS系统及设备采购安装项目。 \n  二、项目编号：ZZ83827HW0003-0438 \n  三、资金来源及采购预算：预算内资金19.7919万元。 \n  四、本项目落实政府采购政策 \n  五、采购内容： \n   \n    \n     \n     包号 \n     产品名称 \n     数量 \n     单价（元） \n     \n     \n     1 \n     江南、江北两个校区安装HIS系统 \n     1项 \n     100000 \n     \n     \n     2 \n     网络电话综合布线设备采购及安装 \n     1项 \n     97919 \n     \n     \n     合计 \n     197919 \n     \n    \n   \n  具体详见竞谈文件。 \n  六、交货期： 合同签订之日起30日内完成供货及安装 \n  交货地点：哈尔滨商业大学 \n  七、潜在供应商的资格要求： \n  1、参加本项目谈判的潜在供应商须具备《政府采购法》第二十二条规定的资格条件； \n  2、参加本项目谈判的潜在供应商须在黑龙江省政府采购网注册登记并经审核合格； \n  3、参加本项目谈判的潜在供应商须具备履约本项目的能力； \n  4、本项目不接受联合体投标。 \n  八、竞谈文件获取方式及时间： \n  潜在供应商请于2018年12月27日至 2018年12月29日，每日上午9时至16时（北京时间，休息时间除外）在中资国际招标有限责任公司获取竞谈文件，逾期不予受理。 \n  文件售价500 元/包，售后不退，采购代理机构只接受通过以上方式获取竞争性谈判文件的供应商的谈判响应文件。 \n  或登录中招联合招标采购平台(http://www.365trade.com.cn)（电子招标投标交易平台名称）注册、购买并下载电子招标文件，（若需纸质招标文件，开标时在代理公司领取）（提示：请购标人考虑完成在线注册、审核所需的时间成本，确保在招标文件发售截止时间前成功购买下载招标文件）。如有疑问可拨打平台统一服务热线400-092-8199咨询。 \n  招标文件每包售价人民币500 元，平台下载费用50元（平台公司出票），售后不退。 \n  九、提交谈判响应文件截止时间及谈判时间：2019年1月3日10时，所有响应文件应在截止时间前密封送达中资国际招标有限责任公司（哈市南岗区汉水路76-6）地点，逾期送达或者未按要求密封的响应文件，招标代理机构拒收，响应文件登记时须携带购买文件的收据进行核实，未购买竞谈文件的视为投标无效。 \n  十、发布谈判邀请的媒介：本次招标公告在黑龙江省政府采购网、哈尔滨商业大学校园网上发布。 \n  采购人：哈尔滨商业大学 \n  地址：哈尔滨市松北区学海街1号 \n  联系人：刘老师 \n  电话：0451-84892112 \n  采购代理机构：中资国际招标有限责任公司 \n  地址：哈尔滨市南岗区汉水路76-6号 \n  邮 政 编 码：150090 \n  项目负责人：谷女士  \n  电话：0451-81888888 \n   传真：0451-88188888 \n   \n \n', '0', '0', '0', '2018-12-26 00:00:00', null, '0', null, '0', null);
