INSERT INTO `post_info` VALUES ('241', '2', '1', '哈尔滨商业大学高效节能环保校园改造工程竞争性磋商公告', null, '\n  \n  中资国际招标有限责任公司受哈尔滨商业大学的委托和采购人提出的采购需求，对哈尔滨商业大学高效节能环保校园改造工程项目进行竞争性磋商，现欢迎具有相应资质且具有施工能力的供应商参加本项目的磋商活动。 \n  一、项目名称：哈尔滨商业大学高效节能环保校园改造工程  \n  二、项目编号：SC[2018]0097/ZZ80113FW0003-0008 \n  三、资金来源及采购预算：预算内资金440万元。 \n  四、工程内容： \n   \n   \n    \n     \n     产品名称 \n     金额（元） \n     \n     \n     第一包 \n     690000 \n     \n     \n     容积式换热器系统节能改造 \n     690000 \n     \n     \n     第二包 \n     1202800 \n     \n     \n     中央空调系统改造 \n     1202800 \n     \n     \n     第三包 \n     1900000 \n     \n     \n     B区游泳馆池水及淋浴加热系统节能改造 \n     1900000 \n     \n     \n     第四包 \n     607200 \n     \n     \n     江北校区蒸汽发生器节能改造 \n     607200 \n     \n     \n     合计 \n     4400000 \n     \n    \n   \n   \n   \n  具体内容详见磋商文件。 \n  五、评审方法：综合评分法。 \n  六、工期：参照第三部分工程范围及技术要求日历天。 \n  工程地点：哈尔滨商业大学。  \n  七、潜在供应商的资格要求： \n   1、参加本项目磋商的潜在供应商必须具备《政府采购法》第二十二条规定的资格条件； \n  2、参加本项目磋商的潜在供应商须在黑龙江省政府采购网注册登记并经审核合格； \n  3、参加本项目磋商的潜在供应商须具备参照第三部分工程范围及技术要求； \n  4、本项目不接受联合体投标； \n  八、投标登记方式及时间： \n  有意向参加本项目投标的潜在供应商，如资格条件符合本项目“潜在供应商的资格要求”并确定参加本次投标活动，须凭用户名和密码登录黑龙江省政府采购网，点击“网上报名”，选定拟参与项目“进入”，选择投报采购包点击“报名”，则报名成功。报名时间：自2018年4月4日至2018年4月11日17时。只有在黑龙江省政府采购网报名成功的潜在供应商，方有资格参加本项目的投标活动。 \n  九、招标文件获取方式： \n  报名成功后请登录黑龙江省政府采购网“下载中心”的“招标采购文件”下载招标文件。 \n  十一、本项目需要现场勘察，请各投标人自行前往进行勘察，勘查时间：2018年4月12日9时，勘查地点：哈尔滨商业大学行政楼前，勘查联系人：张老师，联系电话：13304618853   . \n  十二、提交竞争性磋商首次响应文件截止时间及磋商时间：2018年4月23日14时，所有响应文件应在截止时间前密封送达中资国际招标有限责任公司指定地点，逾期送达或者未按要求密封的响应文件，招标代理机构拒收。 \n  十三、提交竞争性磋商响应文件地点：中资国际招标有限责任公司一楼招标大厅。 \n  十四、授权代表须持本人身份证原件亲自提交竞争性磋商响应文件，并办理签收手续，否则拒收响应文件。 \n  十五、发布公告的媒介：本次招标公告在中国政府采购网、黑龙江省政府采购网上同时发布。 \n  采购人：哈尔滨商业大学 \n  地址：哈尔滨市松北区学海街1号 \n  联系人：唐老师 \n  电话：0451-84892031  \n  采购代理机构： 中资国际招标有 \n   \n   \n \n', '0', '0', '0', '2018-04-04 00:00:00', null, '0', null, '0', null);
