INSERT INTO `post_info` VALUES ('252', '2', '1', '哈尔滨商业大学验室废液、废旧过期药品及动物尸体无害化处置项目单一来源采购公告', null, '\n  \n  根据教学实验设备管理中心的采购申请，招标办拟对哈尔滨商业大学实验室废液、废旧过期药品及动物尸体无害化处置项目单一来源采购项目予以公示。 \n  一、采购人：哈尔滨商业大学 \n  二、项目名称:哈尔滨商业大学_实验室废液、废旧过期药品及动物尸体无害化处置项目 \n  三、采购预算：62000.00元。 \n  四、服务期限：1年。 \n  五、售后服务要求：按照合同要求进行售后服务。 \n  六、质量保证期：按合同要求进行质保。 \n  七、申请理由：经专家论证，本项目符合哈尔滨市环保局及相关部门的要求，按照黑龙江省环保厅及哈尔滨市环保局备案具有合法处置危险废物及医疗废物单位的管理规定，只能从唯一供应商处采购。鉴于行业的特殊性，申请单一来源采购。专家论证意见见附件。 \n  八、单一来源供应商：哈尔滨国环医疗固体废物无害化集中处理中心有限公司 \n  九、公示期：2018年3月26日至2018年3月30日（五个工作日） \n  十、发布公示的媒介：哈尔滨商业大学校园网。 \n  联系人：刘老师 电话：0451-84892112 \n  地址：哈尔滨市松北区学海街1号 \n  如有其他潜在供应商对本项目采用单一来源方式采购有异议，可以在本公示期内以书面形式向哈尔滨商业大学招标管理办公室（联系电话：0451－84892112，联系地址：哈尔滨市松北区学海街一号，邮编：150028）提出意见。 \n   \n   \n   \n  哈尔滨商业大学招标管理办公室 \n  2018年3月26日 \n   \n   \n   \n \n', '0', '0', '0', '2018-03-26 00:00:00', null, '0', null, '0', null);
