INSERT INTO `post_info` VALUES ('325', '2', '1', '我校南区旅游烹饪学院实习基地等14个房间对外招租公告', null, '\n  \n  为进一步加强我校非办公类资产管理，根据有关规定，经学校研究同意，并报上级主管部门批准，我校对南区旅游烹饪学院实习基地等14个房间的房产进行调整后委托黑龙江联合产权交易所再次挂牌。 \n  有意向承租者请详询黑龙江联合产权交易所。 \n  地址：哈尔滨市松北区世茂大道66号（松北万达对面）。 \n  咨询电话：0451-58894170 \n  网址： http://www.huaee.net \n  公告期限：2017年11月14日至2017年12月4日止。 \n  国有资产管理处 \n  2017年11月14日 \n \n', '0', '0', '0', '2017-11-14 00:00:00', null, '0', null, '0', null);
