INSERT INTO `post_info` VALUES ('345', '2', '1', '哈尔滨商业大学2016-2017学年度拟获“国家奖学金”学生名单公示', null, '\n  \n  根据黑龙江省教育厅学生资助管理中心下发的通知精神，为加强奖学金评定工作的公开、公平、公正原则，经过各学院评审公示、学校评审工作小组审核，现将哈尔滨商业大学2016-2017学年度拟获“国家奖学金”学生名单（见附件）予以公示。 \n  在公示期内，任何单位和个人均可通过来电、来访等形式，向哈尔滨商业大学学生工作部（处）反映公示对象的情况和问题。 \n  公示时间：2017年10月10日--2017年10月12日 \n  受理单位：学生工作部（处） \n  联系人：曲毅（A区行政中心523室） \n  联系电话：0451-84865263 \n  附件：哈尔滨商业大学2016-2017学年度拟获“国家奖学金”学生名单 \n  学生工作部（处） \n  2017年10月10日 \n \n', '0', '0', '0', '2017-10-10 00:00:00', null, '0', null, '0', null);
