INSERT INTO `post_info` VALUES ('350', '2', '1', '哈尔滨商业大学A区燃气工程项目单一来源采购公告', null, '\n  \n  受黑龙江省政府采购办公室的委托，拟对哈尔滨商业大学燃气工程项目单一来源采购项目予以公示。 \n  一、采购人：哈尔滨商业大学 \n  二、项目名称: 哈尔滨商业大学_燃气工程 \n  三、采购预算：自筹资金1330478.00元 \n  四、工期：45天。 \n  五、交付使用时间：按照合同约定时间交付使用。  \n  六、售后服务要求：按照合同要求进行售后服务。  \n  七、质量保证期： 按合同要求进行质保。 \n  八、申请理由：经专家论证，本项燃气改造工程项目符合《中华人民共和国政府采购法》第三十一条及黑财采【2017】4号的文件规定，按照哈尔滨市城市规划，属于供暖、供气、煤气等采购项目，只能从唯一供应商处采购。鉴于行业的特殊性，申请单一来源采购。 \n  九、单一来源供应商：黑龙江天辰燃气有限责任公司 \n  十、公示期：2017年9月22日至2017年9月28日（五个工作日） \n  十一、发布公示的媒介：黑龙江省政府采购网上发布。 \n  十二、采购单位：哈尔滨商业大学 \n  联系人：唐德权 电话：0451-84892031 \n  地址：哈尔滨市松北区学海街1号 \n  如有其他潜在供应商对本项目采用单一来源方式采购有异议，请在公示期内将质疑书原件（含电子版）及法人代表授权书原件送达采购办，未在规定时间内送达的质疑为无效质疑。（“质疑文件标准格式”请到下载中心“招标采购文件”中下载） \n  质疑受理：黑龙江省财政厅政府采购管理办公室 \n  联系人：刘佩玉 电话：0451-53650829 \n  地址：哈尔滨市南岗区民益街80号 \n  邮编：150001 \n  哈尔滨商业大学 \n  2017年9月22日 \n \n', '0', '0', '0', '2017-09-22 00:00:00', null, '0', null, '0', null);
