INSERT INTO `post_info` VALUES ('401', '2', '1', '哈尔滨商业大学燃煤锅炉转让项目竞争性谈判公告', null, '\n  \n  招标编号：JZDH-2017027 \n  1.招标条件 \n  北京建智达工程管理股份有限公司受哈尔滨商业大学的委托对哈尔滨商业大学燃煤锅炉转让项目进行国内公开竞争性谈判招标，现欢迎国内合格的投标人参加投标。 \n  2.项目概况与招标范围 \n  2.1项目名称：哈尔滨商业大学燃煤锅炉转让项目 \n  2.2转让地点：哈尔滨商业大学院内 \n  2.3工期：合同签订生效后7日 \n  2.4招标范围：煤锅炉共6台（其中SHW6型号2台，DZL4-1.25型号1台，DZL4-1.25型号1台，DZC1-0.7型号1台，KZL-4型号1台）废旧锅炉及相关辅机拆除、外运、回收，具体以现场实物为准。 \n  3.投标人资格要求 \n  3.1投标人必须在中华人民共和国注册的法人单位或其他组织； \n  3.2具有有效的营业执照、组织机构代码证、税务登记证（若投标单位营业执照为新版三证合一营业执照，则不再需要提供税务登记证及组织机构代码证。）（营业执照经营范围包含废品回收）； \n  3.3本项目不接受联合体投标。 \n  4.投标登记获取招标文件 \n  4.1凡有意参加投标者，请于2017年6月14日至2017年6月16日法定公休日、法定节假日除外)，每日上午9时00分至11时00分，下午14时00分至16时00分(北京时间，下同)，到北京建智达工程管理股份有限公司201室（哈尔滨市道里区群力第六大道4439号）进行投标登记，以便获取招标文件，逾期不予受理。 \n  4.2招标文件每套售价500元，售后不退。 \n  4.3潜在投标人在投标登记时需携带以下材料原件及加盖公章的复印件： \n  （1）法定代表人授权委托书； \n  （2）委托代理人身份证； \n  （3）营业执照； \n  （4）企业组织机构代码证； \n  （5）税务登记证； \n  （6）基本账户开户许可证； \n  （7）无行贿犯罪档案查询函； \n  潜在投标人应对所提供材料的合法性、真实性、完整性负责。 \n  5.投标文件的递交 \n  5.1投标文件递交的截止时间(投标截止时间，下同)为详见招标文件，地点为北京建智达工程管理股份有限公司三楼会议室（哈尔滨市道里区群力第六大道4439号）。 \n  5.2逾期送达的或者未送达指定地点的投标文件，招标人不予受理。 \n  6.发布公告的媒介 \n  本次招标公告在： \n  中国采购与招标网（http://www.chinabidding.com.cn）上发布。 \n  7.其他 \n  7.1投标人将通过《检察机关行贿犯罪档案查询系统》对项目投标企业、其法定代表人近3年行贿犯罪档案进行查询，如有违法违规记录的，不得参与本项目投标； \n  7.2根据《中华人民共和国招标投标法实施条例》有关规定，潜在投标人或者其他利害关系人对本招标公告有异议的，应当在投标登记受理期间以书面形式向招标人提出，书面异议材料应由法定代表人或授权委托代理人签字并加盖单位公章，招标人将在收到异议之日起3日内作出答复。潜在投标人或者其他利害关系人捏造事实、伪造材料进行异议和恶意缠诉的，将严格按照国家及省市有关规定严肃处理，给他人造成损失的，依法承担赔偿责任。 \n  8.联系方式 \n  招 标 人：哈尔滨商业大学 \n  招标代理机构：北京建智达工程管理股份有限公司 \n  地 址：哈尔滨市道里区群力第六大道4439号 \n  邮 编：150070 \n  联 系 人：陆先生 \n  电 话：0451-51016150 \n  电子邮件：bjjzd1@163.com \n \n', '0', '0', '0', '2017-06-13 00:00:00', null, '0', null, '0', null);
