INSERT INTO `post_info` VALUES ('420', '2', '1', '哈尔滨商业大学建设教师公寓（公共租赁住房）地下车库快速硬质卷帘门采购安装竞争性谈判邀请公告', null, '\n  \n  招标编号：HTC -竞172009 \n  1、招标条件 \n  哈尔滨商业大学建设教师公寓（公共租赁住房）项目地下车库快速卷帘门项目已由相关部门批准，采购人为商业大学，建设资金来源为建设单位自筹。 \n  2、项目概况与招标范围 \n  2.1项目名称：哈尔滨商业大学建设教师公寓（公共租赁住房）项目地下车库快速卷帘门采购安装 \n  2.2项目所在地：哈尔滨市松北区三环路以南、学海路以西 \n  2.3招标内容：洞口尺寸8000mmX3000mm 2个洞口，具体参数见竞谈文件。 \n  2.4施工期：2017年8月10日前完成安装 . \n  3、投标人资格要求 \n  3.1投标人具有独立法人资格，并持有有效的营业执照，投标人应为货物的制造商。 \n  3.2本项目不接受联合体投标。 \n  4、谈判文件的获取 \n  请于2017年5月27日-2017年6月3日，每天上午9：00-11：00时，下午13：30-16:00时，在黑龙江省招标公司（哈尔滨市南岗区汉水路180号306室）登记并购买谈判文件。报名时请携带授权委托书、法人身份证复印件、营业执照等材料的原件及加盖公章的复印件。 \n  文件每套售价人民币200.00元，售后不退 \n  5、投标文件的递交 \n  6.1报价文件递交的截止时间（投标截止时间）为2017年6月8日14时，地点为哈尔滨市南岗区汉水路180号2楼会议室。 \n  6、发布公告的媒介 \n  本公告在中国采购与招标网上发布 \n  7、联系方式 \n  招标人：哈尔滨商业大学 \n  招标代理机构：黑龙江省招标公司 \n  地 址：哈尔滨市南岗区汉水路180号 \n  联系人：申明慧 \n  邮 编：150090 \n  电 话：0451-82364710、82364715 \n  电子信箱：hljzb2@126.com \n  日期：2017年5月27日 \n \n', '0', '0', '0', '2017-05-27 00:00:00', null, '0', null, '0', null);
