INSERT INTO `post_info` VALUES ('466', '2', '1', '哈尔滨商业大学灭火器采购及维修项目竞争性谈判公告', null, '\n  \n  北京建智达工程管理股份有限公司受哈尔滨商业大学的委托对哈尔滨商业大学灭火器采购及维修项目进行国内公开竞争性谈判招标，现欢迎国内合格的投标人参加投标。 \n  一、项目名称：哈尔滨商业大学灭火器采购及维修 \n  二、招标编号：JZDH-2017014  \n  三、资金性质：自有资金 \n  四、采购内容 \n  \n   \n    \n     时间 \n     类别 \n     名称 \n     数量 \n    \n    \n     四月 \n     维修 \n     干粉灭火器（2KG、4KG、8KG）2201具 二氧化碳灭火器525具 \n     共计2726具 \n    \n    \n     维修 \n     灭火车（70KG、35KG） \n     70KG （6辆） 35KG（12辆） \n    \n    \n     二公斤 \n     干粉灭火器 \n     550具 \n    \n    \n     二氧化碳 \n     灭火器（2KG） \n     100具 \n    \n    \n     灭火毯 \n     灭火毯（1.5米*1.5米） \n     30件 \n    \n    \n     十月 \n     维修 \n     干粉灭火器（2KG、4KG、8KG） \n     共计1366具 \n    \n    \n     总计 \n      \n      \n     4760 \n    \n   \n  \n  五、交付使用时间：按采购人要求。 \n  六、交货地点：按采购人指定地点。 \n  七、投标人的资格要求 \n  7.1拟参加本项目的潜在投标人须具备有效时间内的中华人民共和国企业法人营业执照、组织机构代码证、税务登记证、（三证合一的只须提供有效的营业执照）企业基本账户开户许可证。 \n  7.2投标人所经销的灭火器有效的《中国国家强制性产品认证证书》和国家消防装备质量监督检验中心出具的《检验报告》。 \n  7.3所经销的灭火器必须有正式注册商标、品牌，并提供相关证明材料。 \n  7.4拟参加本项目的潜在投标人必须为制造商或制造商合法授权的代理商，如为代理商投标，须提供制造商针对本项目的唯一授权。 \n  7.5所经销的灭火器必须在中国消防产品质量信息查询系统www.cccf.com.cn中可查到，产品来源可追溯到。 \n  7.6投标人将自行通过《检察机关行贿受贿档案查询系统》对投标企业及其企业法定代表人行贿受贿档案进行查询，如有上述违法违规记录的，不得参与本项目投标。 \n  7.7本项目资格审查方式为资格后审。 \n  八、获取招标文件方式、时间及地点： \n  8.1有意向的投标人可于2017年4月14日至4月20日每天上午9:00时至11：00时、下午14：00时至16：00时(北京时间，节假日除外) 携带法定代表人授权委托书、法定代表人身份证复印件、授权委托人身份证、营业执照、组织机构代码证、税务登记证、企业基本账户开户许可证、增值税发票开票信息、厂家授权书（代理商提供）、中国国家强制性产品认证证书、国家消防装备质量监督检验中心出具的《检验报告》、无犯罪行贿查询函等上述资料的原件及复印件加盖公章，在哈尔滨市道里区群力第六大道4439号北京建智达工程管理股份有限公司报名及购买招标文件。 \n  8.2招标文件售价：500元/套，售后不退。 \n  九、投标截止时间及开标时间：详见招标文件。 \n  十、递交投标文件地点及开标地点：北京建智达工程管理股份有限公司三楼开标大厅（哈尔滨市道里区群力第六大道4439号），逾期送达的或不符合规定的投标文件将被拒绝接受。 \n  十一、本次招标公告在中国采购与招标网（http://www.chinabidding.com.cn/）上发布。 \n  采购单位：哈尔滨商业大学 \n  联系人：唐德权 \n  地 址：哈尔滨市松北区学海街1号 \n  联系方式：0451-84892031 \n  采购代理机构：北京建智达工程管理股份有限公司 \n  地 址：哈尔滨市道理区群力第六大道4439号 \n  项目联系人：陆超  \n  联系电话：0451-51016150 \n \n', '0', '0', '0', '2017-04-14 00:00:00', null, '0', null, '0', null);
