INSERT INTO `post_info` VALUES ('500', '2', '1', '哈尔滨商业大学电气燃气两用热水器采购竞争性谈判邀请公告', null, '\n  \n  招标编号：HTC-竞161031 \n  黑龙江省招标公司受哈尔滨商业大学的委托对其哈尔滨商业大学电气燃气两用热水器采购项目进行竞争性谈判。欢迎具有此项能力的供应商前来报价。 \n  一、项目名称：哈尔滨商业大学电气燃气两用热水器采购 \n  二、项目编号：HTC-竞161031 \n  三、采购内容： \n  （一）、产品需求表 \n  \n   \n    \n     项目 \n     容量（升） \n     功率 \n     尺寸 \n     数量(台) \n    \n    \n     1 \n     23 \n     380v3KW \n     1120*560*430 \n     3 \n    \n    \n     1160*560*560 \n     2 \n    \n    \n     2 \n     40 \n     380v6KW \n     1160*560*560 \n     1 \n    \n    \n     3 \n     80 \n     380v12KW \n     1160*560*560 \n     14 \n    \n   \n  \n   \n  \n  （二）、参数要求: \n  1.整体采用优质不锈钢材质，多箱式结构，冷水箱独立设置，将生水与开水完全分离，箱体壁厚度1.2mm及以上，加热器为全紫铜管，水龙头为全不锈钢旋钮式。 \n  2.电气燃气两用，电压380v。在突发停电状态下，在二次开启时，可使用燃气功能。 \n  四、交付使用时间：合同签订后按哈尔滨商业大学要求时间内交付。 \n  交货地点：哈尔滨商业大学指定地点 \n  五、供应商的资格要求 \n  (1)供应商必须具有有效的供营业执照（经营范围具有厨房设备）、组织机构代码证、税务登记证。 \n  (2)供应商应具备两年及以上经销燃气锅炉的销售经验（业绩需提供销售合同）。 \n  供应商在购买谈判文件时必须提供上述证明文件的复印件、单位介绍信及法定代表人授权书。 \n  六、获取谈判文件方式、时间及地点：有意向的供应商可从2016年11月29日至12月1日每天(节假日除外)9时至11时30分、13时至15时30分(北京时间)在黑龙江省招标公司303室购买谈判文件。 \n  七、谈判文件售价：每套200.00元人民币（邮购须另加50.00元人民币），谈判文件售后不退。 \n  八、报价截止时间及谈判时间：2016年12月5日13时30分（北京时间）。供应商应在此之前将密封的报价文件送达黑龙江省招标公司二楼会议室，逾期送达的或不符合规定的报价文件将被拒绝接受。 \n  九、报价及谈判地点：哈尔滨市南岗区汉水路180号二楼会议室 \n  招标人：哈尔滨商业大学 \n  地 址：哈尔滨市松北区学海街1号 \n  联系人：赵洪波 \n  电 话：0451-84892032 \n  招标代理机构：黑龙江省招标公司 \n  地 址：哈尔滨市南岗区汉水路180号 邮 编：150090 \n  联系人：张慧清 徐国良  \n  电子信箱：hljszb1@163.com \n  电 话：0451-82364702\\82364703  \n  开户名称：黑龙江省招标公司 \n  开户银行：招商银行股份有限公司哈尔滨开发区支行 \n  联 行 号：308261032030 \n  账 号：451902390110302 \n  黑龙江省招标公司 \n  2016年11月28日 \n \n', '0', '0', '0', '2016-11-28 00:00:00', null, '0', null, '0', null);
