INSERT INTO `post_info` VALUES ('559', '2', '1', '基础科学学院系列学术沙龙活动通知', null, '\n  \n  为把“两学一做”学习教育活动落到实处，营造我院良好的学术研究氛围，发挥博士教师在学术研究中的领军作用，激励青年教师积极参加学术研究的热情，基础科学学院中文教研室开展系列学术沙龙活动。具体安排如下： \n  一、沙龙主题：学术研究经验交流与成果汇报 \n  二、沙龙主办单位：基础科学学院中文教研室 \n  三、沙龙主讲人 \n  1.博士教师代表：王翠荣、郑敏、曲冰 \n  讲座时间：2016年5月19日13:30——16:00 \n  2.在读博士教师代表：赵伟东、叶晓庆 \n  讲座时间：2016年6月2日13:30——16:00 \n  3.青年教师代表：王丽丽、陈广宁、李楠、刘杨 \n  讲座时间：2016年6月16日13:30——16:00 \n  四、讲座地点：B04号楼四楼基础科学学院会议室 \n  欢迎广大师生莅临！ \n  基础科学学院\n   \n  2016年5月18日 \n \n', '0', '0', '0', '2016-05-18 00:00:00', null, '0', null, '0', null);
