INSERT INTO `post_info` VALUES ('676', '2', '1', '哈尔滨商业大学2014年上半年公开招聘在编硕士面试通知', null, '\n  \n  根据省人力资源与社会保障厅的规定及学校工作的实际需要，按照《2014年黑龙江省省直事业单位公开招聘人员公告（2号）》和《哈尔滨商业大学2014年上半年公开招聘教师公告》的招聘要求，面向社会进行公开招聘工作。网上报名、资格审查、现场确认、笔试考试等环节均已完成，现将进行面试工作。具体情况如下： \n  一、面试地点：哈尔滨商业大学设计艺术学院3楼，地址：哈市松北区学海街1号哈尔滨商业大学江北校区A区。 \n  二、面试时间：2015年1月8日（星期四） \n  三、面试要求： \n  1.参加面试考生应为进入面试且公示无异议人员。 \n  2.参加面试考生应于1月8日早8:30前到设计艺术学院4306-4教室集合，抽取面试顺序号。 \n  3.参加面试考生应携带身份证、笔试准考证，辅导员岗位还须携带面试准考证。 \n  四、公交方式：可在公路大桥站乘坐88、119路公交车，在“军安小区”站下车。 \n  哈尔滨商业大学人事处 \n  2015年1月5日 \n \n', '0', '0', '0', '2015-01-05 00:00:00', null, '0', null, '0', null);
