INSERT INTO `post_info` VALUES ('685', '2', '1', '2015年美国大学生数学建模竞赛报名通知', null, '\n  \n  2015年美国大学生数学建模竞赛，将于北京时间2015年2月6日早上8:00—2月10日早上8:00举行，即日起开始报名工作，请准备参赛的同学注意以下报名事宜： \n  1.报名对象： \n  参加过14年国赛建模，或在以往建模竞赛中获过奖的各学院全日制本科生。 \n  2.报名时间：2014年11月17日——11月27日 \n  3.报名方式： \n  美赛要求提交英文论文，组队时要兼顾英语翻译能力，可跨学院组队报名。具体报名：先将报名信息（姓名、性别、学院、年级专业、学号、手机号）按组发到zhjx_19@163.com；再于11月27日（周四）下午到基础科学学院数学教研室（B04楼206室）核实报名信息并交报名费。 \n  4.报名费：每人240元（含注册报名费每组100美元、国际快递论文的费用等），获得二等奖及以上返还报名费，另有学校奖励。 \n  5.报名结束后，学院将组织美赛建模的赛前培训，请及时关注相关信息（哈商大数学建模QQ群号：301453285）。 \n  6.竞赛期间，学院原则上会为各组分配专门指导教师，具体安排根据报名情况再行确定。 \n  主办单位：教务处 \n  承办单位：基础科学学院 \n \n', '0', '0', '0', '2014-11-20 00:00:00', null, '0', null, '0', null);
