INSERT INTO `post_info` VALUES ('7', '2', '1', '关于2018年度省级优秀团员、优秀团干部、五四红旗团委（团支部）拟推荐情况的公示', null, '\n  \n  各学院团委： \n  按照团省委《关于申报2018年度“全省优秀共青团员”“全省优秀共青团干部”“全省五四红旗团委（团支部）”的通知》的工作部署，经校团委召集各学院团委联合推荐，结合2018年度我校共青团各项工作的评优结果，拟推荐以下个人、集体参评省级优秀团员、优秀团干部、五四红旗团委（团支部），具体名单如下： \n  拟推荐会计学院团委参评“全省五四红旗团委”； \n  拟推荐管理学院会展经济与管理2016级1班团支部参评“全省五四红旗团支部”； \n  拟推荐任婵媛同志（轻工学院原团委书记，2018年6月任校团委副书记）参评“全省优秀共青团干部”； \n  拟推荐田佳奇同学（校学生会主席）、王钰超同学（英才学院学生会主席）参评“全省优秀共青团员”。 \n  另：拟推荐任婵媛同志参评“全国优秀共青团干部”；  \n  拟推荐田佳奇同学参评“全国优秀共青团员”。 \n  公示时间：2019年3月13日—3月19日 （5个工作日） \n  公示电话：0451—84865190 \n  公示邮箱：kuailetuanban@163.com \n  现予以公示，如有异议，请在公示期内通过来访、来函、来电等方式向校团委反映。反映情况时要自报或签署真实姓名，并有具体事实根据；不报或不签署真实姓名的，以及不提供具体事实材料的，一律不予受理。 \n   \n  共青团哈尔滨商业大学委员会 \n  2019年3月13日 \n   \n \n', '0', '0', '0', '2019-03-13 00:00:00', null, '0', null, '0', null);
