INSERT INTO `post_info` VALUES ('709', '2', '1', '关于2014年公开招聘人事代理工作人员现场确认的通知', null, '\n  \n  \n   根据哈尔滨商业大学2014年公开招聘人事代理工作人员的工作安排，现就现场确认的相关事宜通知如下： \n  \n  \n   一、现场确认人员范围 \n  \n  \n   本次公开招聘人事代理工作人员现场确认的人员范围为资格审查通过的应聘人员。 \n  \n  \n   二、时间、地点 \n  \n  \n   1.时间：10月26日（周日），上午8:30—11:30。 \n  \n  \n   2.地点：哈尔滨商业大学南校区主楼二楼会议室（房间号213）哈市道里区通达街138号 \n  \n  \n   3.公交方式：可乘坐9、32、75、77、82、92、114、119路公交车，在“哈商大南校区”站下车。 \n  \n  \n   三、现场确认需携带的材料 \n  \n  \n   1.本人签字的《哈尔滨商业大学应聘人事代理工作人员登记表》（见附件1）、身份证及复印件。 \n  \n  \n   2.各层次学历的毕业证书、学位证书原件及复印件各一份；应届毕业生须由研究生管理部门开具在读证明和能够如期毕业并获得学位的证明。各层次学历学习成绩单各一份（须加盖成绩管理章；档案材料复印件须加盖档案部门公章）。 \n  \n  \n   海外留学回国人员除国内院校学历证书外，还须有国外院校毕业证书、留学回国人员证明、教育部留学回国人员学历认证。 \n  \n  \n   3.政审材料，即应聘人员在学习、工作期间的思想政治、学习情况、业务水平能力的鉴定，并填写《哈尔滨商业大学公开招聘人事代理工作人员思想政治鉴定表》（见附件2）。 \n  \n  \n   4.两张近期一寸彩色免冠照片。 \n  \n  \n   5.其他证明材料。 \n  \n  \n   四、注意事项 \n  \n  \n   携带材料不全或未按照规定时间参加现场确认的考生视为自动放弃，取消考试资格。 \n  \n  \n   五、联系方式 \n  \n  \n   联系人：富老师 刘老师 \n  \n  \n   咨询电话：(0451)84895450 \n  \n   \n  \n   \n  \n  \n   哈尔滨商业大学人事处 \n  \n  \n   2014年10月23日 \n  \n   \n  \n   \n  \n  \n   附件1-哈尔滨商业大学应聘人事代理工作人员登记表 \n  \n  \n   附件2-哈尔滨商业大学公开招聘人事代理工作人员思想政治鉴定表 \n  \n   \n   \n \n', '0', '0', '0', '2014-10-23 00:00:00', null, '0', null, '0', null);
