INSERT INTO `post_info` VALUES ('751', '2', '1', '美国密苏里大学堪萨斯分校宋熊熊教授专题讲座通知', null, '\n  \n  讲座主题：如何在国际顶级期刊上发表论文 \n  主 讲 人：宋熊熊教授 \n  宋熊熊教授现为美国密苏里大学堪萨斯分校讲席教授、终身教授、系主任、研究所长，同时兼任荷兰屯特大学科学顾问和新加坡南洋理工大学兼职教授。2007年被美国Journal of Product Innovation Management期刊评为世界排名第一的创新管理学者；2006年被Essential Science Indicators评选为10年间被引用最多的世界前100名经济学与管理学研究学者之一，被R&amp;D Management期刊列为世界前20名的技术管理研究者之一；2004年和2009 年两度被国际技术管理协会列为技术创新管理领域研究成果最多的学者之一。 \n  研究方向主要集中在服务创新、新兴市场营销战略、技术创业及新创立企业和新兴技术估值。发表文章100余篇，其中UTD列出的管理类顶级期刊发表论文近20篇。 \n  时 间：2014年6月19日（周四）下午14:30 \n  地 点：C区管理学院楼310会议室 \n  欢迎全校老师、硕博士研究生们踊跃参加并现场交流。 \n \n', '0', '0', '0', '2014-06-17 00:00:00', null, '0', null, '0', null);
