INSERT INTO `post_info` VALUES ('759', '2', '1', '《太阳岛讲坛》第157期讲座预告暨青年企业家进校园第五讲', null, '\n  \n  讲座主题：雪娃的成长路 \n  讲座嘉宾：张春楠 \n  讲座时间：2014年6月5日（周四）17:00\n   \n  讲座地点：南区主楼408室  \n  主 办：校团委 \n  承 办：校学生会 \n   校大学生科技协会 \n  嘉宾简介：张春楠，雪娃文化公司总经理。哈尔滨雪娃文化发展有限公司坐落于黑龙江动漫产业（平房）发展基地，公司拥有2000平米的生产场地和可使用的顶级的影视图形工作站，专业的渲染机房、编辑机房、录音棚和演播室等国内高水准先进制作设备。公司具有多年成熟工作经验的骨干人员组成，完整的策划创作团队，雪娃文化主要从事动漫原创设计、动漫外包、动漫广告设计、动漫游戏设计、大学生实习和大学生就业见习基地等业务，创意制作技术达到国内先进水平 。 \n    \n  欢迎广大师生聆听讲座！！！ \n \n', '0', '0', '0', '2014-06-04 00:00:00', null, '0', null, '0', null);
