INSERT INTO `post_info` VALUES ('766', '2', '1', '国家京剧院高雅艺术进校园演出公告', null, '\n  \n  为响应国家教育部、文化部、财政部联合举办的“高雅艺术进校园”活动，由省教育厅组织，我校团委承办的国家京剧院专场演出将于5月29日在我校举行。  \n  演出主题：构建高雅校园文化国粹艺术唱响高校  \n  演出曲目：《望江亭》 \n  演出时间：2014年5月29日17:00  \n  演出地点：东区大礼堂  \n  演出单位：国家京剧院  \n  协办单位：共青团哈尔滨商业大学委员会  \n  欢迎广大师生前来欣赏！  \n \n', '0', '0', '0', '2014-05-28 00:00:00', null, '0', null, '0', null);
INSERT INTO `post_info` VALUES ('767', '2', '1', '哈尔滨商业大学节能减排社会实践与科技竞赛评审结果公示', null, '\n  \n  各学院团委：  \n  哈尔滨商业大学节能减排社会实践与科技竞赛经过专业评审，最终评出科技类一等奖2项、二等奖4项、三等奖5项、优秀奖3项，社会实践类一等奖1项、二等奖1项、三等奖2项、优秀奖1项。我校将获三等奖以上作品报送至第七届全国大学生节能减排社会实践与科技竞赛，进行国家比赛。现公布如下： \n  \n   \n    \n     科技类 \n    \n    \n     奖项设置 \n     项目名称 \n    \n    \n     一等奖 \n     二次利用节水装置 \n    \n    \n     基于建筑空心结构的太阳能驱动多能 自然通风空调系统 \n    \n    \n     二等奖 \n     利用尾气余热的混合驱动车载制冷系统 \n    \n    \n     小型电动碎冰机的设计与开发 \n    \n    \n     具有手机无线充电功能的柔性薄膜太阳能包 \n    \n    \n     跳舞毯脚踏式发电台灯 \n    \n    \n     三等奖 \n     新型电化学生物耦合处理含油污泥装置 \n    \n    \n     动圈式噪声发电装置 \n    \n    \n     汽车重力势能收集与电能转换装置 \n    \n    \n     太阳能教学楼 \n    \n    \n     .夜间安全出口指示发光pvc薄膜 \n    \n    \n     优秀奖 \n     易开启自锁井盖 \n    \n    \n     水循环及自然冷却塔原理在南方楼房中制冷发电的应用 \n    \n    \n     新型环保铅笔的开发  \n    \n    \n     社会实践类 \n    \n    \n     一等奖 \n     基于“高校毕业生简历浪费现象”的研究与方案 \n    \n    \n     二等奖 \n     寝室日常用水的再利用模型及其经济意义 \n    \n    \n     三等奖 \n     秸秆处理问题调查分析 \n    \n    \n     高校大学生教科书循环使用的调查研究及方案 \n    \n    \n     优秀奖 \n     反射天然光室内升温增亮系统 \n    \n   \n  \n  共青团哈尔滨商业大学委员会 \n  2014年5月26日 \n \n', '0', '0', '0', '2014-05-27 00:00:00', null, '0', null, '0', null);
