INSERT INTO `post_info` VALUES ('891', '2', '1', '《太阳岛讲坛》第111期讲座预告暨“职来直往”首届大学生模拟招聘会启动仪式', null, '\n  \n  讲座主题：带你走进职场，体验美好未来 \n  讲座嘉宾：申晨 \n  讲座时间：2013年4月14日（周日） 15:30-17:30 \n  讲座地点：专家公寓学术报告厅 \n  主 办：校团委 \n  承 办：校学生会、校大学生科技协会 \n  嘉宾简介：中国教育电视台《职来职往》节目点评嘉宾，新浪微博高级人力资源经理。具有8年人力资源管理经验，毕业指导讲座《饭碗》被誉为最受学生欢迎的讲座。尤其擅长为职场人的心理健康、职业生涯及职场中面临的种种困惑提供专业指导，被很多职场人亲切地称为“职场知音”。他点评选手时一针见血，往往会为他们打开一扇属于自己的门。 \n  欢迎大家聆听讲座！ \n \n', '0', '0', '0', '2013-04-09 00:00:00', null, '0', null, '0', null);
