INSERT INTO `post_info` VALUES ('893', '2', '1', '基础科学学院讲座通知', null, '\n  \n  \n   哈尔滨商业大学基础科学学院讲座 \n   讲座题目： 在实践中成长 \n   ——谈我在参加挑战杯、创新创业大赛中的点滴体会 \n   讲座嘉宾： 刘 珏 \n   嘉宾简介： 哈尔滨工业大学、人文学院09级学生。获第十二届“挑战杯”全国大学生课外学术科技作品竞赛三等奖；申请的全国大学生创业实践项目获批经费全校最高且排名第一。该项目被学校选派为代表队伍参加由教育部、科技部主办的全国大学生创新创业年会，并代表哈尔滨工业大学参赛。在来自全国160余所高校的196个队伍中位列第二，获第五届全国大学生创新创业年会“我最喜爱的项目”奖。 \n   讲座时间： 2013年4月1日 17:00-19:00 \n   讲座地点： 专家公寓一楼学术报告厅 \n   基础科学学院 \n   2013年3月29日 \n   \n \n', '0', '0', '0', '2013-03-28 00:00:00', null, '0', null, '0', null);
INSERT INTO `post_info` VALUES ('894', '2', '1', '《太阳岛讲坛》第110期暨第十三届挑战杯大学生科技作品竞赛第二期培训预告', null, '\n  \n  讲座主题：挑战杯作品的分析与解答 \n  讲座嘉宾：马兴盛 教授 \n  讲座时间：2013年3月31日 08:30-10:30 \n  讲座地点：3105教室 \n  主 办：校团委 \n  承 办：校大学生科技协会 \n  嘉宾简介：教授，硕士研究生导师，学科带头人，先后在国内著名刊物上发表论文数篇和出版教材数本。主要从事食品及保健产品的研究与开发，在产品概念、生产技术、管理、财务等方面有着丰富的理论、实践经验和独到见解。所指导的作品多次在“挑战杯”大学生课外科技学术作品竞赛、“挑战杯”大学生创业计划竞赛中荣获国家级、省级奖项。 \n  欢迎大家聆听讲座！ \n \n', '0', '0', '0', '2013-03-28 00:00:00', null, '0', null, '0', null);
