INSERT INTO `post_info` VALUES ('901', '2', '1', '药学院拟举办“新思路、新视野”青年研究者论坛系列活动 第三季第一期', null, '\n  \n   药学院拟举办“新思路、新视野”青年研究者论坛系列活动 \n  第三季第一期 \n  药学院自2012年5月至今，已经举办了第一季和第二季“新思路、新视野”青年研究者论坛系列活动，论坛活动以药学院青年教师、研究生及本科生科研创新小组为主体，每期设有不同的主题，以论坛为平台，通过分享经验、学术交流、达到拓宽科研思路，开阔科研视野目的。今年我院在2013年3月至6月期间启动第三季“新思路、新视野”青年研究者论坛系列活动，2013年3月18日下午药学院刘琳博士和王宝庆博士将走进论坛与广大师生一起交流课题研究经验和心得，欢迎广大师生踊跃参加。 \n  药学院“新思路、新视野”青年研究者论坛系列活动第三季第一期 \n  论坛时间：2013年3月18日下午13:30 \n  活动地点：药学院实验楼407 \n  题目1：芎麻滴丸对实验性血管性认知障碍发病轨迹的动态干预研究 \n  主讲人：刘琳博士  \n  题目2：肿瘤生物治疗 \n  主讲人：王宝庆博士  \n \n', '0', '0', '0', '2013-03-15 00:00:00', null, '0', null, '0', null);
