INSERT INTO `post_info` VALUES ('91', '2', '1', '哈尔滨商业大学北校区实验综合楼10KV变电所工程施工招标公告', null, '\n  \n  招标编号：HTC-182233 \n  1. 招标条件 \n  哈尔滨商业大学北校区实验综合楼10KV变电所工程工程施工已由相关部门批准，招标人为哈尔滨商业大学，建设资金来自预算资金。 \n  2. 项目概况与招标范围 \n  2.1项目名称：哈尔滨商业大学北校区实验综合楼10KV变电所工程工程施工 \n  2.2建设地点：松北区学海街1号哈尔滨商业大学B区校园内； \n  2.3建设规模：高低压配电柜、10KV电缆铺设、10KV线路间隔部分。 \n  2.4计划工期：2018年11月10日到2018年12月29日，总工期50日历天。 \n  2.5质量标准：满足国家及相关行业合格标准，并负责验收送电。 \n  2.6招标范围：工程量清单及施工图纸所示全部内容。 \n  3. 投标人资格要求 \n  3.1 本次招标要求投标人须需具备建设行政主管部门颁发的电力工程施工总承包三级及以上资质，同时具备国家电力监管委员会颁发的电力设施承装五级及以上许可证，并在人员、设备、资金等方面具有相应的履约能力。 \n  3.2投标人拟派的项目经理应具备机电工程专业贰级及以上建造师注册证及有效的安全生产考核合格证书，且未担任其他在施建设工程项目的项目经理. \n  3.3本次招标不接受联合体投标。 \n  3.4与招标人存在利害关系可能影响招标公正性的法人、其他组织或者个人，不得参加投标；单位负责人为同一人或者存在控股、管理关系的不同单位，不得同时参加同一标段投标或者未划分标段的同一招标项目投标；同一公司具有独立法人的子公司同时参加同一标段投标或者未划分标段的同一招标项目投标时最多不得超过两家（均以获取文件的先后顺序为准）。 \n  3.5资格审查方式：本工程采用资格后审方式,主要资格审查标准、内容等详见招标文件，只有资格审查合格的投标申请人才有可能被授予合同。 \n  3.6其他 \n  3.6.1投标企业及其法定代表人、拟派项目负责人近三年有行贿犯罪行为的不得参与本项目投标（以中国裁判文书网查询结果为准）。 \n  3.6.2投标人如被列为失信被执行人，不得参与本项目投标（以中国执行信息公开网查询结果为准）。 \n  4.招标文件的获取 \n  4.1凡有意参加投标者，请于2018年10月16日至2018年10月22日(法定公休日、法定节假日除外)，每日上午9:00时至11:00时，下午13:30时至16:30时(北京时间，下同)，到黑龙江省招标公司（哈尔滨市南岗区汉水路180号）306室获取招标文件，逾期不予受理。 \n  4.2招标文件每套售价700元，售后不退。 \n  4.3潜在投标人在获取招标文件时需携带以下材料原件及复印件，复印件加盖投标人公章： \n  （1）法定代表人身份证（只需复印件）及授权委托人身份证及授权委托书； \n  （2）营业执照； \n  （3）资质证书； \n  （4）安全生产许可证 \n  （5）基本账户开户许可证 \n  （6）项目经理身份证（只需复印件）、注册建造师证及安全生产考核证； \n  （7）投标人失信被执行人名单查询网上截图（以中国执行信息公开网查询为准）及投标人、法定代表人及拟派项目经理的无行贿犯罪行为查询网上截图（以中国裁判文书网查询结果为准）。 \n  （8）拟投施工员、质量员的岗位证书、安全员Ｃ类人员安全生产考核证（各至少配备一人） \n  5.投标文件的递交 \n  5.1投标文件递交的截止时间(投标截止时间)为2018年11月6日9:00，地点为黑龙江省招标公司（哈尔滨市南岗区汉水路180号）二楼会议室。 \n  5.2逾期送达的或者未送达指定地点的投标文件，招标人不予受理。 \n  6、发布公告的媒介本公告在中国招标投标公共服务平台上发布7、联系方式 \n  招 标 人：哈尔滨商业大学 \n  地 址：松北区学海街1号哈尔滨商业大学B区校园内 \n  联 系 人：赵洪波 \n  电 话：0451-84892032 \n  招标代理机构：黑龙江省招标公司 \n  地 址：哈尔滨市开发区汉水路180号 \n  联 系 人：申明慧 周岩青 \n  电 话： 0451-82364710 82364715 \n  电子邮件：hljzb2@126.com \n  开户银行：招商银行股份有限公司哈尔滨开发区支行  \n  账号：451902390110302 \n   \n \n', '0', '0', '0', '2018-10-16 00:00:00', null, '0', null, '0', null);
