INSERT INTO `post_info` VALUES ('913', '2', '1', '药学院拟举办“科研整合、科研传承”博士导师系列讲座第二讲', null, '\n  \n   药学院拟举办“科研整合、科研传承”博士导师系列讲座第二讲 \n  药学院拟2012年11月22日举办“科研整合、科研传承”博士导师系列讲座第二讲，各位博士导师将针对自己的研究方向，就已经研究的内容和将要研究的内容给大家进行系统的阐述，总结科研实践经验，指导青年教师发挥自己的优势，寻找科研方向、提升科研思路，实现新老教师的科研传承，带动青年教师科研水平的提升，药学院“科研整合、科研传承”博士导师系列讲座第二讲由张翠教授、张秀娟教授、阎力君教授主讲，欢迎广大师生参加。 \n  时间：2012年11月22日（周四）上午10:00 \n  地点：药学院407 \n  讲座人：张翠教授 张秀娟教授 阎力君教授 \n \n', '0', '0', '0', '2012-11-20 00:00:00', null, '0', null, '0', null);
