INSERT INTO `post_info` VALUES ('963', '2', '1', '黑龙江中医药大学李永吉教授来药学院进行专家讲坛第二讲', null, '\n  \n  药学院专家学术讲坛系列活动 \n  第二讲 \n  2012年8月31日，药学院拟进行专家学术讲坛系列活动第二讲，邀请到黑龙江中医药大学现任药学院院长兼药剂教研室主任、中药学学科后备带头人、药剂学学科带头人李永吉教授，李永吉教授主要研究方向为中药注射剂及中药经皮给药。在国内率先提出“中药注射剂活性分子组合”研制思路，并在“心肌炎康注射液”的研制及制备工艺中应用。欢迎广大师生参加专家学术讲坛活动。 \n  讲座题目：中药注射剂临床安全性问题分析及对策 \n  讲座时间：2012年8月31日下午13:30 \n  讲座地点：药学院实验楼407 \n  讲座人：李永吉教授 \n \n', '0', '0', '0', '2012-08-29 00:00:00', null, '0', null, '0', null);
