INSERT INTO `post_info` VALUES ('97', '2', '1', '学术讲​座—新时代公平竞争审查制度的经济法解读', null, '\n  \n   \n   \n   \n   学术讲座  \n   题 目：新时代公平竞争审查制度的经济法解读 \n   主 讲 人：孙晋 教授  \n   讲座时间：2018年10月15日（星期一） 18时  \n   讲座地点：北区教学主楼0117室（模拟法庭） \n   主办单位：哈尔滨商业大学法学院 \n   黑龙江省法学会经济法学研究会 \n    \n   孙晋简介:安徽六安人，法学博士，武汉大学法学院教授、博士生导师，湖北省十大杰出法学家，加州大学伯克利分校法学院访问学者，新疆大学“天山学者“，中国经济法研究会常务理事，中国商业法研究会常务理事，武汉大学与原国家发改委价监反垄断局合作基地:武汉大学竞争法与竞争政策研究中心主任，武汉大学网络治理研究院执行院长，湖北省人民政府法律顾问，中国世贸组织研究会竞争法律与政策委员会专家，武汉市法学会竞争法研究会会长。主持国家社科基金项目、教育部规划基金一般项目、教育部后期资助项目、中国法学会部级研究项目、湖北省社科基金重点项目等近50项，在《中国法学》、《法商研究》、《现代法学》、《法学评论》、《人民日报》、《光明日报》等中外报刊公开发表论文近120篇，出版专著10部、主编教材8本。 \n    \n   欢迎广大师生届时莅临！ \n   \n   \n \n', '0', '0', '0', '2018-10-14 00:00:00', null, '0', null, '0', null);
