INSERT INTO `post_info` VALUES ('973', '2', '1', '哈尔滨商业大学举办第九届太阳岛博士论坛的通知', null, '\n  \n  经济转型发展与管理创新 \n   ——哈尔滨商业大学第九届太阳岛博士论坛 \n  2012年适逢哈尔滨商业大学建校60周年。作为校庆活动重要组成部分的“太阳岛博士论坛”，将于2012年8月24日—26日在美丽的冰城夏都哈尔滨隆重举行。诚邀全国高校经管学科博士研究生参会。 \n  本届论坛的主题是“经济转型发展与管理创新”，旨在“以学术促交流，以沟通促合作”。论坛邀请国内著名专家学者作主题报告，本届论坛与会博士的优秀论文将推荐到经管类核心期刊发表。 \n  本届论坛免收会务费，参会者的食宿、会议资料及参观考察费用均由哈尔滨商业大学承担，往返交通费用自理。论坛具体事宜，请登陆哈尔滨商业大学研究生学院网站：http://yjsc.hrbcu.edu.cn \n  联 系 人：朱凤义博士 \n  联系方式：0451-84892351（办）；15134566336 \n  taiyangdaoluntan@163.com \n    \n    \n   哈尔滨商业大学太阳岛博士论坛组委会 \n   二〇一二年七月一日 \n \n', '0', '0', '0', '2012-07-04 00:00:00', null, '0', null, '0', null);
